package robert.li.asolution;

public class A_567_checkInclusion {
    public static void main(String[] args) {
        String s1 = "xuumbjffxuzovdwrnolopeingppzgorotzdqfprokkmucxwsub";
        String s2 = "";
        A_567_checkInclusion a_567_checkInclusion = new A_567_checkInclusion();
        boolean b = a_567_checkInclusion.checkInclusion(s1, s2);
    }


    public boolean checkInclusion(String s1, String s2) {
        char[] chars1 = s1.toCharArray();
        char[] chars2 = s2.toCharArray();
        if(chars2.length < chars1.length) return false;
        int charsCount = 0;
        int[] arr = new int[26];
        for (int i = 0; i < chars1.length; i++) {
            if (arr[chars1[i] - 'a'] == 0) {
                arr[chars1[i] - 'a'] = 1;
                charsCount++;
            } else {
                arr[chars1[i] - 'a']++;
            }
        }
        int tmpCount = 0;
        int[] tmpArr = new int[26];
        int length = s1.length();
        for (int i = 0; i < length; i++) {
            tmpArr[chars2[i] - 'a']++;
            if (arr[chars2[i] - 'a'] != 0 && arr[chars2[i] - 'a'] == tmpArr[chars2[i] - 'a']) {
                tmpCount++;
            } else
            if (arr[chars2[i] - 'a'] != 0 && arr[chars2[i] - 'a'] + 1 == tmpArr[chars2[i] - 'a']) {
                tmpCount--;
            }
        }
        if(charsCount == tmpCount) return true;
        for (int i = length; i < s2.length(); i++) {
            tmpArr[chars2[i] - 'a']++;
            if (arr[chars2[i] - 'a'] != 0 && arr[chars2[i] - 'a'] == tmpArr[chars2[i] - 'a']) {
                tmpCount++;
            } else
            if (arr[chars2[i] - 'a'] != 0 && arr[chars2[i] - 'a'] + 1 == tmpArr[chars2[i] - 'a']) {
                tmpCount--;
            }
            tmpArr[chars2[i - length] - 'a']--;
            if (arr[chars2[i - length] - 'a'] != 0 && arr[chars2[i - length] - 'a'] == tmpArr[chars2[i - length] - 'a']) {
                tmpCount++;
            } else if (arr[chars2[i - length] - 'a'] != 0 && arr[chars2[i - length] - 'a'] - 1 == tmpArr[chars2[i - length] - 'a']) {
                tmpCount--;
            }
            if(charsCount == tmpCount) {
                return true;
            }
        }
        return false;
    }

}
